import React, { useState } from 'react';
import {Button, Grid} from '@mui/material';
import TodoInput from './TodoInput';
import TodoList from './TodoList';
import { ToDoItemProps } from './index.d';
import './index.css';

const Index = () => {
    const [listData, setListData] = useState<ToDoItemProps[]>([
        {
            id: '001',
            name: '初始数据1',
            checked: false
        }, {
            id: '002',
            name: '初始数据2',
            checked: true
        }, {
            id: '003',
            name: '初始数据3',
            checked: false
        }
    ]);
  const notDoneCount = listData.filter(item => !item.checked).length;
  const handleAddTodo = (value: string) => {
    const id = Date.now().toString();
    const newValue = {
      id,
      name: value,
      checked: false
    }
    setListData([
      newValue,
      ...listData,
    ])
  }
  return (
     <div className='wrap'>
    <TodoInput onEnter={(value: string) => handleAddTodo(value)} />
    <TodoList todos={listData} handleDone={(id: string) => {

      const newListData = listData.map(item => {
        if(item.id === id) {
          item.checked = true;
        }
        return item;
      })

      setListData(newListData);
    }} />
    <div className='info'>
        <div>{notDoneCount.toString()} items left</div>
        <div>
        {
          !notDoneCount ? <Button>All Active Completed</Button> : null
        }
        </div>


        <Button onClick={() => {
          const newListData = listData.filter(item  => !item.checked);
          setListData(newListData);
        }}>Clear Completed</Button>
        </div>
</div> 
  );
}

export default Index;
