import React, { useEffect, useState } from 'react';
import {Button, Alert} from '@mui/material';
import CheckIcon from '@mui/icons-material/Check';
import TodoInput from './TodoInput';
import TodoList from './TodoList';
import { ToDoItemProps } from './index.d';
import './index.css';

const Index = () => {
    const [listData, setListData] = useState<ToDoItemProps[]>([
        {
            id: '001',
            name: '初始数据1',
            checked: false
        }, {
            id: '002',
            name: '初始数据2',
            checked: true
        }, {
            id: '003',
            name: '初始数据3',
            checked: false
        }
    ]);
    const [cacheData, setCacheData] = useState<ToDoItemProps[]>([]);
    const [showAlert, setShowAlert] = useState<Boolean>(false);
    const [allDone, setAllDone] = useState<Boolean>(false);
  const notDoneCount = listData.filter(item => !item.checked).length;
  const handleAddTodo = (value: string) => {
    const id = Date.now().toString();
    const data = [
      {
        id,
        name: value,
        checked: false
      },
      ...listData
    ]
    setListData(data);
  }
  useEffect(() => {
    if(allDone) {
      setTimeout(() => {
        setShowAlert(false);
      }, 3000)
    }
  }, [allDone])
  return (
     <div className='wrap'>
      {
        allDone && showAlert ? <Alert icon={<CheckIcon fontSize="inherit" />} severity="info">
        All Active Completed !
  </Alert>:null
      }

    <TodoInput onEnter={(value: string) => handleAddTodo(value)} />
    <TodoList todos={listData} handleDone={(id: string) => {

      const newListData = listData.map(item => {
        if(item.id === id) {
          item.checked = true;
        }
        return item;
      })
      const doneCount = newListData.filter(item => item.checked).length;
      debugger
      const isAll = doneCount === newListData.length;
      if(isAll) {
        setShowAlert(true);
      }
      setAllDone(isAll);

      setListData(newListData);
    }} />
    <div className='info'>
        <div>{notDoneCount.toString()} items left</div>


        <Button onClick={() => {
          if(allDone) {
            setCacheData(listData);
            const newListData = listData.filter(item  => !item.checked);
            setListData(newListData);
          } else {
            setListData(cacheData);
          }
        }}>{allDone ? "Clear Completed" : "Show All"}</Button>
        </div>
</div> 
  );
}

export default Index;
